int rows = grid.size();
int cols = grid[0].size();
int padding = (80 - rows * 2) / 2;
void printBoard(){
    for(int i=0;i<rows;i++){
        for(int j=0;j<cols;j++){
            cout<<grid[i][j];
        }
        cout<<endl;
    }
}
int pX = 4;
int pY = rows - 4;
void printPlayer(){
    grid[pY][pX] = 'P';
}
void erasePlayer(){
    grid[pY][pX] = ' ';
}
void movePlayerRight(){
    if (pX < cols - 1 && grid[pY][pX + 1] != '#') {
        pX++;
    }
}
void movePlayerLeft(){
    if (pX > 0 && grid[pY][pX - 1] != '#') {
        pX--;
    }
}
int bX = pX;
int bY = pY;
void printBullet(){
    grid[bY][bX] = '^';
    bY--;
}
void eraseBullet(){
    grid[bY][bX] = '^';
}
void fire(){
    printBullet();
    Sleep(10);
    eraseBullet();
}
int main(){
    while(true){
        system("cls");
        erasePlayer();
        if (GetAsyncKeyState(VK_LEFT)) movePlayerLeft();
        if (GetAsyncKeyState(VK_RIGHT)) movePlayerRight();
        if (GetAsyncKeyState(VK_SPACE)) fire();
        printPlayer();
        printBoard();
    }
    
    return 0;
}       